import React, { useEffect, useState } from 'react'
import logo from './logo.svg'
import './App.css'

function App() {
	const [image, imageSet] = useState([])

	let getImage = () => {
		fetch('/dicom')
			.then((response) => JSON.parse(response))
			.then((data) => imageSet(data))
	}

	useEffect(() => {
		getImage()
	}, [])

	let createImage = () => {
		console.log(typeof image)
		const canvas = document.getElementById('canvas')
		const ctx = canvas.getContext('2d')
		var imgData = ctx.createImageData(image[0].length, image.length)
		for (let i = 0; i < imgData.data.length; i += 4) {
			imgData.data[i + 0] = image[i]
			imgData.data[i + 1] = image[i + 1]
			imgData.data[i + 2] = image[i + 2]
			imgData.data[i + 3] = image[i + 4]
		}
		ctx.putImageData(imgData, 0, 0)
	}

	useEffect(() => {
		if (image !== [] && image.length > 0) {
			createImage()
		}
	}, [image])

	return (
		<div className='App'>
			<header className='App-header'>
				<img src={logo} className='App-logo' alt='logo' />
				<p>
					Edit <code>src/App.js</code> and save to reload.
				</p>

				<canvas id='canvas'></canvas>
			</header>
		</div>
	)
}

export default App
