import React, { useEffect, useState } from 'react'
import logo from './logo.svg'
import './App.css'
import * as cornerstone from 'cornerstone-core'
import * as cornerstoneMath from 'cornerstone-math'
import * as cornerstoneTools from 'cornerstone-tools'
import * as cornerstoneWebImageLoader from 'cornerstone-web-image-loader'

function App() {
	const [image, imageSet] = useState([])

	let getImage = () => {
		fetch('/dicom')
			.then((response) => response.json())
			.then((data) => imageSet(data))
	}

	useEffect(() => {
		getImage()
	}, [])

	let createImage = () => {
		const element = document.getElementById('canvas')
		cornerstone.enable(element)
		cornerstone.loadImage(image).then(function (test) {
			cornerstone.displayImage(element, test)
		})
		// console.log(image.length)
		// const canvas = document.getElementById('canvas')
		// const ctx = canvas.getContext('2d')
		// var imgData = ctx.createImageData(1000, 1000)
		// for (let i = 0; i < imgData.data.length; i += 4) {
		// 	imgData.data[i + 0] = image[i]
		// 	imgData.data[i + 1] = image[i + 1]
		// 	imgData.data[i + 2] = image[i + 2]
		// 	imgData.data[i + 3] = 255
		// }
		// ctx.putImageData(imgData, 0, 0)
	}

	useEffect(() => {
		if (image !== []) {
			createImage()
		}
	}, [image])

	return (
		<div className='App'>
			<header className='App-header'>
				<img src={logo} className='App-logo' alt='logo' />
				<p>
					Edit <code>src/App.js</code> and save to reload.
				</p>

				<canvas id='canvas'></canvas>
			</header>
		</div>
	)
}

export default App
